exports.userData = (obj) => {
  const id = Math.random().toString(16).substring(2, 10) + Date.now().toString(16).substring(9);
  const changeFirstLetter = (str) => str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();

  const [firstName, lastName] = obj.name.split(' ');
  const birthDate = new Date(obj.dateBirth).getFullYear();
  const carrentDate = new Date().getFullYear();

  return {
    id,
    firstName: changeFirstLetter(firstName),
    lastName: changeFirstLetter(lastName),
    dateBirth: obj.dateBirth,
    age: carrentDate - birthDate,
    purpose: changeFirstLetter(obj.purpose),
  }
}
